import Api from "./api";

export const openEditProfilePopupButton = document.querySelector('.profile__button-edit');
export const closeEditProfilePopupButton = document.querySelector('.popup__close_type_edit');
export const openAddCardPopupButton = document.querySelector('.profile__button-add');
export const popupAddCard = document.querySelector('.popup_type_add');
export const popupEditProfile = document.querySelector('.popup_type_profile');
export const closeAddCardPopupButton = document.querySelector('.popup__close_type_add');
export const closePopupPreviewButton = document.querySelector('.popup__close_type_preview');
export const formPopupProfile = document.querySelector('.popup__form_type_edit');
export const formPopupAdd = document.querySelector('.popup__form_type_add');
export const nameInput = document.querySelector('.popup__item_type_name');
export const jobInput = document.querySelector('.popup__item_type_job');
export const title = document.querySelector('.profile__title');
export const subtitle = document.querySelector('.profile__subtitle');
export const popupAvatar = document.querySelector('.popup_type_avatar');
export const openPopupAvatar = document.querySelector('.profile__foto-edit');
export const formPopupAvatar = document.querySelector('.popup__form_type_avatar');
export const userAvatar = document.querySelector('.profile__foto');
export const escape = 'Escape';

export const config = {
    url: 'https://mesto.nomoreparties.co/v1/cohort-25',
    headers: {
        authorization: 'df3e4aab-6899-4784-852c-de3c6ef6b3bc',
        'Content-Type': 'application/json'
    }
};
